import React, { useState } from 'react';
import axios from 'axios';

function SignIn() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = { email, password, role };
        try {
            await signIn(data);
        } catch (error) {
            console.error("Error during sign in", error);
        }
    };

    async function signIn(data) {
        try {
            const response = await axios.post('http://localhost:8000/signin', data);
            const { token } = response.data;

            // Vous pouvez stocker le token dans le localStorage ou dans les cookies pour le conserver entre les sessions
            localStorage.setItem('token', token);

            // Redirigez l'utilisateur vers la page d'accueil ou une autre page appropriée
            // Vous pouvez utiliser la bibliothèque react-router pour cela, par exemple :
            // history.push('/home');
        } catch (error) {
            // Gérez les erreurs ici. Par exemple, si les identifiants sont incorrects, vous pourriez
            // vouloir afficher un message d'erreur à l'utilisateur
            console.error(error);
        }
    }


    return (
        <form onSubmit={handleSubmit}>
            <label>
                Email:
                <input
                    type="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    required
                />
            </label>
            <label>
                Password:
                <input
                    type="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    required
                />
            </label>
            <label>
                Role:
                <select value={role} onChange={e => setRole(e.target.value)}>
                    <option value="">Select...</option>
                    <option value="admin">Admin</option>
                    <option value="user">User</option>
                </select>
            </label>
            <button type="submit">Sign In</button>
        </form>
    );
}

export default SignIn;
