// src/components/FileUpload.js
import React, { useCallback, useState } from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext';
import './FileUpload.css';

const FileUpload = () => {
  const [isDragActive, setIsDragActive] = useState(false);

  const handleDragEnter = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragActive(true);
  }, []);

  const handleDragLeave = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragActive(false);
  }, []);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
  }, []);

  const { authToken } = useAuth();

  const handleDrop = useCallback(
    async (e) => {
      e.preventDefault();
      e.stopPropagation();
      setIsDragActive(false);

      const file = e.dataTransfer.files[0];
      const formData = new FormData();
      formData.append('files', file);

      // Ajout des fichiers à FormData
      // for (let i = 0; i < files.length; i++) {
      //   formData.append('files', files[i]);
      // }

      // backend upload url
      const uploadUrl = 'http://127.0.0.1:8000/upload';

      try {
        const response = await fetch(uploadUrl, {
          method: 'POST',
          body: formData,
          headers: { 
            'Authorization': `Bearer ${authToken}`
          },
        });

        if (!response.ok) {
          let errorMessage = 'Erreur lors du téléversement des fichiers';
          switch (response.status) {
            case 400:
              errorMessage = 'Erreur du client lors de l\'envoi des fichiers';
              break;
            case 401:
              errorMessage = 'Erreur d\'authentification';
              break;
            case 422:
              errorMessage = 'Fichier non traitable envoyé';
              break;
            default:
              errorMessage = `Erreur inconnue (statut ${response.status})`;
              break;
          }
          throw new Error(errorMessage);
        }

        // Traitement de la réponse du serveur
        const data = await response.json();
        console.log('Fichiers téléversés avec succès:', data);

      } catch (error) {
        console.error('Erreur lors du téléversement des fichiers:', error);
      }      
    },
    [authToken]
  );

  const { isAuthenticated, userRole } = useAuth();

  if (!isAuthenticated || !userRole ==='admin') {
    return <Navigate to="/" />;
  }

  return (
    <div
      className={`dropzone ${isDragActive ? 'active' : ''}`}
      onDragEnter={handleDragEnter}
      onDragLeave={handleDragLeave}
      onDragOver={handleDragOver}
      onDrop={handleDrop}
    >
      {isDragActive ? (
        <p>Déposez vos fichiers ici...</p>
      ) : (
        <p>Faites glisser et déposez vos fichiers ici, ou cliquez pour sélectionner les fichiers</p>
      )}
    </div>
  );
};

export default FileUpload;
